    mailayre-container {
      display: flex;
      flex-direction: column;
      gap: 10px;
      align-items: center;
      text-align: center;
      mailayre-profiles {
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
        gap: 75px;
        padding: 0px 25px;
        a {
          text-decoration: none;
          svg {
            height: 75px;
            width: auto;
          }
          transition: all 0.25s ease;
          @media (hover: hover) and (pointer: fine) {
            &:hover {
              transform: scale(1.1);
            }
          }
        }
      }
      a {
        text-decoration: none;
        background: {
